*/
package main

import (
    "strings"

    "golang.org/x/tour/wc"
)

func WordCount(s string) map[string]int {
    counts := make(map[string]int)
    for _, word := range strings.Fields(s) {
        counts[word] = counts[word] + 1
    }
    return counts
}

func main() {
    wc.Test(WordCount)
