type IPAddr [4]byte

func (ipAddr IPAddr) String() string {
    // Slice of length 0 and capacity 4 (size of array)
    s := make([]string, 0, len(ipAddr))
    for _, v := range ipAddr {
        s = append(s, strconv.Itoa(int(v)))
    }
    return strings.Join(s, ".")
